mapper.select:
SELECT
FROM t_order_info WHERE uid =#{uid}
AND order_type = #{orderType}
ORDER BY create_time DESC

SELECT toi.*, tu.phone as amountPhone FROM t_order_info toi, t_user tu WHERE toi.uid=tu.uid AND toi.order_type=4
AND toi.order_id = #{orderId}
AND tu.phone = #{phone}
AND DATE_FORMAT(toi.create_time,'%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(toi.create_time,'%Y-%m-%d') <= #{endDate}
AND toi.status = 1 AND toi.pay_status =1
AND toi.pay_status in (2,4)
AND toi.status = 2 AND toi.pay_status = 3
ORDER BY toi.create_time DESC

SELECT IFNULL(SUM(toi.total_money), 0) + IFNULL(SUM(toi.postage), 0) totalMoney, IFNULL(SUM(toi.total_pay), 0) totalPay FROM t_order_info toi, t_user tu WHERE toi.uid = tu.uid AND toi.order_type = 4
AND toi.order_id = #{orderId}
AND tu.phone = #{phone}
AND DATE_FORMAT(toi.create_time,'%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(toi.create_time,'%Y-%m-%d') <= #{endDate}
AND toi.status = 1 AND toi.pay_status =1
AND toi.pay_status in (2,4)
AND toi.status = 2 AND toi.pay_status = 3
