mapper.select:
   SELECT
		tub.*, 
		tu.phone
	FROM
		t_user_bill tub,
		t_user tu
	WHERE
		tub.uid = tu.uid

	AND	tub.bill_type = #{billType}


	AND	tub.type = #{type}


	AND tub.order_id = #{orderId}


	AND tu.phone = #{phone}


		AND DATE_FORMAT(tub.create_time,'%Y-%m-%d') >= #{startDate}


	   AND DATE_FORMAT(tub.create_time,'%Y-%m-%d') <= #{endDate}

   SELECT
		IFNULL(SUM(tub.amount), 0) totalPay
	FROM
		t_user_bill tub,
		t_user tu
	WHERE
		tub.uid = tu.uid

	AND	tub.bill_type = #{billType}


	AND	tub.type = #{type}


	AND tub.order_id = #{orderId}


	AND tu.phone = #{phone}


		AND DATE_FORMAT(tub.create_time,'%Y-%m-%d') >= #{startDate}


	   AND DATE_FORMAT(tub.create_time,'%Y-%m-%d') <= #{endDate}
