mapper.select:
SELECT 

FROM
	t_user


		phone = #{phone}


		AND uid = #{uid}


		AND inviter_phone = #{inviterPhone}


		AND inviter_uid = #{inviterUid}


		AND is_manager = #{isManager}


		AND is_vip = #{isVip}


		AND DATE_FORMAT(create_time,'%Y-%m-%d') >= #{startDate}


	   AND DATE_FORMAT(create_time,'%Y-%m-%d') <= #{endDate}
